     const file = Deno.openSync("/foo/bar.txt");
     const buf = new Uint8Array(100);
     const numberOfBytesRead = Deno.readSync(file.rid, buf); // 11 bytes
     const text = new TextDecoder().decode(buf);  // "hello world"
     Deno.close(file.rid);

